window.addEventListener('load', () => {
  setTimeout(() => {
    document.getElementById('intro').style.display = 'none';
    document.getElementById('main-content').classList.add('show-content');
  }, 5000);
});

document.addEventListener('DOMContentLoaded', () => {
  const productos = [
    {
      nombre: "Figura decorativa personalizada",
      imagen: "https://m.media-amazon.com/images/I/71yn-QDJ1QS.jpg",
      precio: "$6.500"
    },
    {
      nombre: "Soporte para aud√≠fonos",
      imagen: "data:image/jpeg;base64,",
      precio: "$4.200"
    },
    {
      nombre: "Llavero con nombre",
      imagen: "data:image/jpeg;base64,",
      precio: "$2.000"
    },
    {
      nombre: "Organizador de cables",
      imagen: "data:image/jpeg;base64,",
      precio: "$3.500"
    },
	{
		nombre: "Servicio Personalizado",
      imagen: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS6OKuPGzs9KdvVUbfHnQz1odSeUczaFaH7Ew&s",
      precio: "convenir"
	}
	
  ];

  const container = document.getElementById('product-list');

  productos.forEach(producto => {
    const card = document.createElement('div');
    card.className = 'product';
    card.innerHTML = `
      <img src="${producto.imagen}" alt="${producto.nombre}" />
      <h3>${producto.nombre}</h3>
      <p>${producto.precio}</p>
      <button onclick="alert('Puedes solicitar este producto por interno. al whatsapp +56936197477 o via correo electronico fabriziobuzz@gmail.com')">Solicitar</button>
    `;
    container.appendChild(card);
  });
});

console.log("Printiverse cargado correctamente");